import '@babel/register';
import 'core-js/stable';
import 'regenerator-runtime/runtime';

import { runCommand, getCommandAndArgvFromCli } from '../lib/commands/helpers';
import { exit } from '../lib/utils/helpers';

/**
 * Main entry point, run md-seed cli
 * @return {Promise}
 */
const run = async () => {
  try {
    // recive the command and the arguments input from the cli
    const { command, argv } = getCommandAndArgvFromCli();

    // run the cli command
    await runCommand(command, argv);

    exit();
  } catch (error) {
    exit(error);
  }
};

run();

import generate from './generate';
import help from './help';
import init from './init';
import run from './run';

/**
 * Available command names
 *
 * Map command key to command name
 * @type {Map<string, string>}
 */
export const commands = {
  GENERATE: 'generate',
  HELP: 'help',
  INIT: 'init',
  RUN: 'run',
};

/**
 * Available command aliases
 *
 * Map alias to command name
 * @type {Map<string, string>}
 */
export const aliases = {
  g: commands.GENERATE,
  h: commands.HELP,
};

/**
 * All available command names as list (includes aliases)
 * @type {string[]}
 */
export const availableCommandsList = [
  null, // no command should run help
  ...Object.values(commands),
  ...Object.keys(aliases),
];

/**
 * Commands map
 *
 * Map command name to the actuall command function
 * @type {Map<string, Function>}
 */
export const commandsMap = {
  [commands.GENERATE]: generate,
  [commands.HELP]: help,
  [commands.INIT]: init,
  [commands.RUN]: run,
};

/**
 * The fefault command name
 * @type {string}
 */
export const defaultCommand = commands.HELP;

import chalk from 'chalk';

import { SeederGenerator } from '../../core';
import { validateUserConfig } from '../../utils/helpers';
import config from '../../config';

/**
 * Generate a new seeder.
 * @param  {string}  name seeder name
 * @return {Promise}
 */
const generateSeeder = async name => {
  validateUserConfig();

  const { seederTemplate, userSeedersFolderPath } = config;

  const generator = new SeederGenerator({
    name,
    seederTemplate,
    userSeedersFolderPath,
  });

  const generatedSeederFile = await generator.generate();

  console.log(`${chalk.green('CREATED')} ${generatedSeederFile}`);
};

export default generateSeeder;

