import commandLineCommands from 'command-line-commands';
import {
  commandsMap,
  aliases,
  defaultCommand,
  availableCommandsList,
} from './constants';

/**
 * Whether a given command is an alias
 * @param  {string}  command
 * @return {Boolean}
 */
export const isAlias = command => Object.keys(aliases).includes(command);

/**
 * Get the command name of a given alias
 * @param  {string} alias
 * @return {string}
 */
export const aliasToCommand = alias => aliases[alias];

/**
 * Get the function of a given command
 * @param  {string}   command command name
 * @return {Function} command function
 */
export const commandToFunction = command => {
  command = command || defaultCommand;

  if (isAlias(command)) {
    command = aliasToCommand(command);
  }

  return commandsMap[command];
};

/**
 * Get the command and the arguments from the cli
 * @return   {Object}
 * @property {string}   command command name
 * @property {string[]} argv    command arguments
 */
export const getCommandAndArgvFromCli = () => {
  const { command, argv } = commandLineCommands(availableCommandsList);

  return { command, argv };
};

/**
 * Run command
 * @param  {string} command command name
 * @param  {string} argv    command arguments
 */
export const runCommand = (command, argv) => {
  const commandFunction = commandToFunction(command);

  return commandFunction(argv);
};

import test from 'ava';
import sinon from 'sinon';

import { aliases, commands, commandsMap, defaultCommand } from './constants';
import {
  isAlias,
  aliasToCommand,
  commandToFunction,
  runCommand,
  getCommandAndArgvFromCli,
  __RewireAPI__ as moduleRewireAPI,
} from './helpers';

test.serial('isAlias should work', t => {
  Object.keys(aliases).forEach(a => t.is(isAlias(a), true));
  Object.keys(commands).forEach(c => t.is(isAlias(c), false));

  t.is(isAlias(), false);
});

test.serial('aliasToCommand should work', t => {
  for (const [alias, command] of Object.entries(aliases)) {
    t.is(aliasToCommand(alias), command);
  }
});

test.serial('commandToFunction should work', t => {
  for (const [command, func] of Object.entries(commandsMap)) {
    t.is(commandToFunction(command), func);
  }

  for (const [alias, command] of Object.entries(aliases)) {
    t.is(commandToFunction(alias), commandToFunction(command));
  }

  t.is(commandToFunction(), commandToFunction(defaultCommand));
});

